INSERT INTO `sub_story_slice` VALUES (197, 6, '{\"text\": \"The grandmother ate the cake and drank the wine that Little Red Riding Hood had brought.\", \"emo_control\": {\"emo_alpha\": 0.7, \"emo_vector\": [0.2, 0, 0, 0, 0, 0, 0, 0], \"emo_vector_reason\": \"祖母享用礼物体现高兴，调整维度0为0.2\"}, \"slice_reason\": \"句号结尾且情感一致（高兴），语义完整\", \"pause_settings\": {\"pause_reason\": \"温馨场景，稍长停顿\", \"interval_silence\": 350}, \"duration_control\": {\"max_mel_tokens\": 720, \"duration_reason\": \"26字中等长度文本，720 tokens匹配自然语速\", \"duration_control\": true}}', 68, '2025-10-28 11:44:47', '2025-10-28 11:44:47', b'0');
INSERT INTO `sub_story_slice` VALUES (198, 6, '{\"text\": \"And Little Red Riding Hood thought to herself: \\\"As long as I live, I will never leave the path and run off into the woods by myself if mother tells me not to.\\\"\", \"emo_control\": {\"emo_alpha\": 0.65, \"emo_vector\": [0, 0, 0, 0, 0, 0, 0, 0], \"emo_vector_reason\": \"反思教训，语气平静，使用默认零向量\"}, \"slice_reason\": \"句号结尾且为故事结尾，语义完整\", \"pause_settings\": {\"pause_reason\": \"故事结尾反思，延长停顿增强收尾感\", \"interval_silence\": 600}, \"duration_control\": {\"max_mel_tokens\": 750, \"duration_reason\": \"48字中等长度文本，750 tokens匹配舒缓语速\", \"duration_control\": true}}', 69, '2025-10-28 11:44:47', '2025-10-28 11:44:47', b'0');
